import React, { Component } from 'react';
import Head from 'next/head';

// About Us Data
import AboutUs from '../../data/aboutus.json';

class AboutUsBlock extends Component {

    constructor(props) {
        super(props);
        this.state = {
            aboutBlock: ''
        }
    }

    static async getInitialProps() {
        const res = await fetch('../../data/aboutus.json');
        const data = await res.json();
        console.log("Data: ", data);
    }

    componentDidMount() {
        this.setAboutBlock();
    }

    setAboutBlock=()=> {
        this.setState({aboutBlock: AboutUs.aboutBlock})
    }

    getAboutBlock=()=> {
        if(this.state.aboutBlock) {
            let address = this.state.aboutBlock.address.map((data, index)=> {
                return(
                    <div key={index}>
                        <h3>{data.heading}</h3>
                        <div className="info-holder">
                            <address>{data.address}</address>
                            <div className="img">
                                <img src={data.image} alt="Image Description" />
                            </div>
                        </div>
                    </div>
                )
            });
            return address;
        }
    }

    getLeafImage=()=> {
        if (this.state.aboutBlock) {
            let data = this.state.aboutBlock;
            return (
                <div className="img-shape">
                    <img src={data.bigLeafImage} alt="Image Description" />
                </div>
            )
        }
    }

    render() {
        return (
            <div>
                <Head>
                    <link href="https://fonts.googleapis.com/css?family=Montserrat:300,400,500,600,700&display=swap" rel="stylesheet" />
                    <link href="/static/style.css" rel="stylesheet" />
                </Head>
                <div className="address-blocks">
                    <div className="container">
                        {this.getAboutBlock()}
                        {this.getLeafImage()}
                    </div>
                </div>
            </div>
        );
    }
}

export default AboutUsBlock;
