import React, { Component } from 'react';
import Head from 'next/head';

// Data
import learnCards from '../data/learn';

class LearningCards extends Component {
    
    constructor(props) {
        super(props);
        this.state = {
            learnCardRenderer: ''
        }
    }

    componentDidMount() {
        this.setLearnCards();
    }

    setLearnCards=()=> {
        this.setState({learnCardRenderer: learnCards});
    }

    getLearnCards=()=> {
        if(this.state.learnCardRenderer.hasOwnProperty("learnCards")) {
            let cardsResult = this.state.learnCardRenderer.learnCards.map((data, index)=>{
                return(
                    <div className="col" key={index}>
                        <div className="img">
                            <img src={data.image} alt="Image description" />
                            <h2>{data.heading}</h2>
                        </div>
                        <p>{data.textDetail}</p>
                        <a  className="btn">
                            {data.detailBtn}
                        </a>
                    </div>
                );
            });
            return cardsResult;
        }
    }

    render() {
        return (
            <div>
                <Head>
                    <link href="/static/style.css" rel="stylesheet" />
                </Head>
                <main className="main">
                    <div className="intro">
                        <h1>Welcome to Learning Lab</h1>
                        <p>Your one-stop-shop for everything you need to become an Aesthetic Record Expert</p>
                    </div>
                    <div className="threecolumns container">
                        {this.getLearnCards()}
                    </div>
                </main>
            </div>
        );
    }
}

export default LearningCards;
